async () => {
  forAllFolders({
    type: "operations",
    basePath: getPathsWithOperations(),
    callback: async (folderPath, index) => {
      const operationName = getLastFolder(folderPath);
      log(
        `#${index}: Clearing Typescript Indexation Data for ${operationName}`,
        { type: "success" }
      );
      await clearTsDatabase(operationName);
      return;
    },
  });
}
