value:
mergeObjectsArray(
  functionApiKeys
    .map((fnName) => fnName as keyof typeof api)
    .map((fnName) => {
      const apiFn = api[fnName];
      const useQueryFunction = (...parameters: any[]) => {
        const queryResult = useQuery(
          [fnName, ...parameters],
          // @ts-ignore
          () => apiFn(...parameters),
          { enabled: true, refetchOnWindowFocus: "always" }
        );
        return queryResult;
      };

      const hookName = camelCase(`use-${fnName}`);
      const objectPart = { [hookName]: useQueryFunction };

      return objectPart;
    })
) as unknown as QueryType
