value:
(
  name: string,
  /**
   * if given, uses this token, otherwise generates a new random token
   */
  previousToken?: string,
  attachTokenToFilename?: boolean
): string => {
  if (!attachTokenToFilename) return name;
  const newRandomToken = generateRandomString(32);
  return `${name}${tokenPrefix}${previousToken || newRandomToken}`;
}
