value:
(
  name: string,
  /**
   * if given, is used for generating new token
   *
   * if already present, token is used from the name instead of this one
   */
  newToken?: string,
  attachTokenToFilename?: boolean
) => {
  const { nameWithoutToken, token } = removeTokenIfPresent(
    name,
    attachTokenToFilename
  );
  const nameWithToken = addToken(
    nameWithoutToken,
    token || newToken,
    attachTokenToFilename
  );
  return nameWithToken;
}
