(asset: Asset) => {
  const extensionFromType =
    asset.type === "audio"
      ? `mp3`
      : asset.type === "image"
      ? `jpeg`
      : asset.type === "video"
      ? `mp4`
      : undefined;

  const extension = extensionFromType
    ? extensionFromType
    : asset.originalFilename
    ? getExtension(asset.originalFilename)
    : undefined;

  return extension;
}
