(urlOrPath: string): AssetType => {
  const extension = getExtension(urlOrPath);
  if (["mp3", "m4a", "wav"].includes(extension)) return "audio";
  if (["mp4", "avi"].includes(extension)) return "video";
  if (["jpeg", "jpg", "png", "gif", "svg"].includes(extension)) return "image";
  if (isText(urlOrPath)) return "text";
  return "other";
}
