export const serverDownloadReply = (
  absoluteAssetPath: string,
  isDownload: boolean
) => {
  const parsedAsset = path.parse(absoluteAssetPath);

  if (isDownload) {
    return server.reply.download(absoluteAssetPath, parsedAsset.base);
  }
  return (
    server.reply
      // @ts-ignore NB: TODO: why doesn't server.js has the right types???
      .file(absoluteAssetPath)
  );
};
