async (
  projectRelativeReferencingFilePath: string,
  referencingFileRelativeAssetPath: string
): Promise<{
  isSuccessful: boolean;
  message: string;
  absoluteAssetPath?: string;
}> => {
  // TODO: STEP 1: ensure the user is authenticated and can access the referencing file

  const projectRoot = getProjectRoot();
  if (!projectRoot)
    return {
      isSuccessful: false,
      message: "No project root",
    };

  const extension = getExtension(projectRelativeReferencingFilePath);
  const allowedExtensions = ["ts", "tsx", "md", "mdx", "json"];
  if (!allowedExtensions.includes(extension)) {
    return {
      isSuccessful: false,
      message: "Invalid extension",
    };
  }

  const absoluteReferencingFilePath = path.join(
    projectRoot,
    projectRelativeReferencingFilePath
  );

  const exists = fs.existsSync(absoluteReferencingFilePath);

  if (!exists) {
    return {
      isSuccessful: false,
      message: "Invalid file",
    };
  }

  const contents = await fs.readFile(absoluteReferencingFilePath, "utf8");

  const patternType = extension === "json" ? "json" : "markdown";

  const pattern =
    patternType === "json"
      ? `"relativePath": "${referencingFileRelativeAssetPath}"`
      : `](${referencingFileRelativeAssetPath})`;

  const foundPattern = contents.includes(pattern);

  if (!foundPattern) {
    // console.log({ contents, pattern });
    return {
      isSuccessful: false,
      message: "Pattern not found",
    };
  }

  const referencingFileFolderPath = path.parse(absoluteReferencingFilePath).dir;

  const absoluteAssetPath = path.join(
    referencingFileFolderPath,
    referencingFileRelativeAssetPath
  );

  if (!fs.existsSync(absoluteAssetPath)) {
    return {
      isSuccessful: false,
      message: "The referenced asset does not exist",
    };
  }

  return { isSuccessful: true, message: "Found path", absoluteAssetPath };
}
