async (
  searchQuery?: string,
  /**
   * defaults to absolute
   */
  returnType?: "projectRelative" | "absolute"
): Promise<string[]> => {
  const projectRoot = getProjectRoot();

  if (!projectRoot) return [];

  const dbPath = path.join(projectRoot, databaseFolderName);

  const operationBasePaths = Object.values(operations).map(
    (projectRelativePath) => path.join(projectRoot, projectRelativePath)
  );

  const databaseFolderPaths = operationBasePaths
    .map((absoluteOperationPath) =>
      path.join(absoluteOperationPath, databaseFolderName)
    )
    .concat(dbPath)
    .filter(fs.existsSync);

  const dbResults = await explore({
    basePath: databaseFolderPaths,
    extension: mediaExtensions,
    search: searchQuery,
  });

  const operationResults = await explore({
    basePath: operationBasePaths,
    extension: mediaExtensions,
    ignore: [...generatedFolders, databaseFolderName, sourceFolderName],
    search: searchQuery,
  });

  const anywhereResults = await explore({
    cancelRecursionOn: pathArrayIsOperation,
    basePath: projectRoot,
    extension: mediaExtensions,
    ignore: [databaseFolderName, "node_modules", ".git"],
  });

  const results = [dbResults, operationResults, anywhereResults]
    .flat()
    .map((x) => x.path);

  return returnType === "projectRelative"
    ? results.map((x) => makeRelative(x, projectRoot))
    : results;
}
