async (
  serverContext: Context
): Promise<{
  isSuccessful: boolean;
  isUnauthorized?: boolean;
  message: string;
}> => {
  if (!serverContext) {
    return { isSuccessful: false, message: "No server context provided" };
  }
  /**
  const authToken = (ctx as any).cookies?.authToken;
  const authToken2 = ctx.cookie?.authToken;

  console.log({
    authToken,
    authToken2,
    cookie: ctx.cookie,
    cookies: (ctx as any).cookies,
    headers: ctx.req.headers,
  });

  const isAuthed = functionAuthPlugin("uploadAsset", authToken);
  if (!isAuthed) {
    return {
      isSuccessful: false,
      isUnauthorized: true,
      message: "You can only do this when authenticated",
    };
  }
  */
  const isDownload = takeFirst(serverContext.query.isDownload) === "1";

  const projectRelativeReferencingFilePath = takeFirst(
    serverContext.query.projectRelativeReferencingFilePath
  );

  const referencingFileRelativeAssetPath = takeFirst(
    serverContext?.query.referencingFileRelativeAssetPath
  );

  const { absoluteAssetPath, message, isSuccessful } =
    await findAbsoluteAssetPathFromReference(
      projectRelativeReferencingFilePath,
      referencingFileRelativeAssetPath
    );

  if (!absoluteAssetPath) {
    return { isSuccessful, message };
  }

  return serverDownloadReply(absoluteAssetPath, isDownload);
}
