async (
  /**
   * The backendAsset that may need processing
   */
  backendAsset?: BackendAsset | BackendAsset[]
): Promise<undefined | BackendAsset> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    console.log("processAsset: No project root");
    return;
  }
  const realBackendASset = backendAsset ? takeFirst(backendAsset) : undefined;

  if (!realBackendASset) {
    console.log("processAsset: No real backend asset");

    return;
  }

  const {
    alt,
    name,
    relativePath,
    temporaryDestination,
    projectRelativeReferencingFilePath,
    modelName,
  } = realBackendASset;

  if (!projectRelativeReferencingFilePath) {
    log(
      `processAsset: projectRelativeReferencingFilePath wasn't provided, not processing asset`,
      { type: "warning" }
    );
    return undefined;
  }

  const absoluteReferencingFilePath = path.join(
    projectRoot,
    projectRelativeReferencingFilePath
  );

  // console.log("processing asset", { backendAsset });
  const slugifiedName = slugify(name && name.length > 0 ? name : "untitled");

  if (!relativePath && !temporaryDestination) {
    log("processAsset: no relativePath, no temporaryDestination", {
      type: "warning",
    });
    // NB: we must have either a relativePath or a temporaryDestination, otherwise this is an invalid input
    return undefined;
  }

  const absoluteTemporaryDestination = temporaryDestination
    ? path.join(getTemporaryAssetsFolderPath(), temporaryDestination)
    : undefined;

  // NB: If a `temporaryDestination` is given, if it doesn't exist, we return nothing, this is an invalid input.
  if (absoluteTemporaryDestination) {
    if (!fs.existsSync(absoluteTemporaryDestination)) {
      log("processAsset: absoluteTemporaryDestination does not exist", {
        type: "warning",
      });

      return undefined;
    }
  }

  const oldAssetStoragePath = relativePath
    ? path.join(path.parse(absoluteReferencingFilePath).dir, relativePath)
    : undefined;

  // NB: if a relativePath is provided without a temporaryDestination, it means the file should already be there. If it's not there, we return nothing, invalid input.
  if (oldAssetStoragePath && !temporaryDestination) {
    if (!fs.existsSync(oldAssetStoragePath)) {
      log(
        `processAsset: oldAssetStoragePath does not exist (${oldAssetStoragePath})`,
        {
          type: "warning",
        }
      );

      return undefined;
    }
  }

  // Now we can assume the extension of the temporarydestination is correct, because it gets put there from upload
  const extension = temporaryDestination
    ? temporaryDestination.split(".").pop()
    : relativePath?.split(".").pop();

  if (!extension) {
    log("processAsset: could not create extension", { type: "warning" });

    // NB: extension must exist now.
    return undefined;
  }

  /*
   */

  const storageLocationInfo = getStorageLocationInfo(
    absoluteReferencingFilePath,
    modelName
  );

  const finalAssetStoragePath = path.join(
    storageLocationInfo.absoluteAssetBaseFolderPath,
    `${slugifiedName}.${extension}`
  );

  let finalAvailableStoragePath: string = finalAssetStoragePath;

  if (absoluteTemporaryDestination) {
    finalAvailableStoragePath = getFirstAvailableFilename(
      finalAssetStoragePath
    );

    await renameAndCreate(
      absoluteTemporaryDestination,
      finalAvailableStoragePath
    );
  } else if (oldAssetStoragePath) {
    if (oldAssetStoragePath !== finalAssetStoragePath) {
      finalAvailableStoragePath = getFirstAvailableFilename(
        finalAssetStoragePath
      );

      // the name has changed
      await renameAndCreate(oldAssetStoragePath, finalAvailableStoragePath);
    }
  }

  const newRelativePath = getRelativeLinkPath(
    absoluteReferencingFilePath,
    finalAvailableStoragePath
  );

  //({ newRelativePath });

  console.log({ alt, relativePath });
  return { alt, relativePath: newRelativePath };
}
