async <KInterfaceName extends keyof DbModels>(
  item: AugmentedAnyModelType,
  interfaceName: KInterfaceName,
  operationName: string | null,
  customQueryConfig: CustomQueryConfig
): Promise<AugmentedAnyModelType | undefined> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) return;
  const absoluteReferencingFilePath = await db.getDbFileLocationPath(
    item,
    operationName,
    interfaceName,
    customQueryConfig
  );
  if (!absoluteReferencingFilePath) return item;

  const projectRelativeReferencingFilePath = makeRelative(
    absoluteReferencingFilePath,
    projectRoot
  );

  const assetParameters = findAssetParametersRecursively(item);

  const assetsToPutOnObject = await Promise.all(
    assetParameters.map(async (assetParameter) => {
      const location = [
        ...(assetParameter.stack || []),
        assetParameter.parameterName,
      ];

      const value = getParameterAtLocation<
        BackendAsset | BackendAsset[] | undefined
      >(item, location);

      const backendAssetToStore = Array.isArray(value)
        ? (
            await Promise.all(
              value.map((asset) =>
                processAsset({
                  ...asset,
                  projectRelativeReferencingFilePath,
                  modelName: interfaceName,
                })
              )
            )
          ).filter(notEmpty)
        : value
        ? await processAsset({
            ...value,
            projectRelativeReferencingFilePath,
            modelName: interfaceName,
          })
        : undefined;

      return { backendAssetToStore, location };
    })
  );

  assetsToPutOnObject.forEach((assetWithLocation) => {
    setKeyAtLocation(
      assetWithLocation.location.join("."),
      assetWithLocation.backendAssetToStore,
      item
    );
  });

  // console.dir(
  //   { assetParameters, assetsToPutOnObject, finalItem: item },
  //   { depth: 999 }
  // );

  return item;
}
