(props: { setBlobs: (blobs: Asset[]) => void }) => {
  const { setBlobs } = props;
  return (
    <Input
      type="file"
      multiple
      onChange={(event) => {
        const files = event.target.files;

        if (!files) {
          setBlobs([]);
          return;
        }

        const fileArray = new Array(files.length)
          .fill(null)
          .map((_, index) => {
            const file = files.item(index);

            return file;
          })
          .filter(notEmpty);

        const assetBlobs: Asset[] = fileArray.map((file) => {
          const type = getTypeFromFileBlob(file);
          const name = withoutExtension(file.name);

          const asset: Asset = {
            blobPath: URL.createObjectURL(file),
            originalFilename: file.name,
            blob: file,
            type,
            name,
            sizeBytes: file.size,
            uploadProgress: 0,
          };

          return asset;
        });
        setBlobs(assetBlobs);
      }}
    />
  );
}
