(
  props: ReactMediaRecorderRenderProps & { type: MediaRecorderType }
) => {
  const {
    status,
    startRecording,
    stopRecording,
    pauseRecording,
    resumeRecording,
    previewAudioStream,
    mediaBlobUrl,
    previewStream,
    type,
    muteAudio,
    unMuteAudio,
    isAudioMuted,
  } = props;

  const AudioOrVideo = type === "audio" ? Audio : Video;

  const buttons =
    status === "acquiring_media"
      ? []
      : status === "recording"
      ? [
          <button key="stop" onClick={stopRecording}>
            ‚èπ
          </button>,
          <button key="pause" onClick={pauseRecording}>
            ‚è∏
          </button>,
          type === "video" || type === "screen" ? (
            isAudioMuted ? (
              <button key="unmute" onClick={unMuteAudio}>
                üîà
              </button>
            ) : (
              <button key="mute" onClick={muteAudio}>
                üîá
              </button>
            )
          ) : undefined,
        ]
      : status === "paused"
      ? [
          <button key="stop" onClick={stopRecording}>
            ‚èπ
          </button>,
          <button key="pause" onClick={resumeRecording}>
            ‚ñ∂Ô∏è
          </button>,
        ]
      : [
          <button key="start" onClick={startRecording}>
            ‚è∫
          </button>,
        ];

  return (
    <div>
      {buttons}
      {mediaBlobUrl && status !== "recording" ? (
        <AudioOrVideo src={mediaBlobUrl} controls autoPlay />
      ) : null}
      {type === "video" && previewStream && status === "recording" ? (
        <VideoPreview stream={previewStream} />
      ) : null}
      {type === "audio" && previewAudioStream && status === "recording"
        ? // TODO: visualise with https://developer.mozilla.org/en-US/docs/Web/API/Web_Audio_API/Visualizations_with_Web_Audio_API
          null
        : null}
    </div>
  );
}
