value:
(props: {
  type: MediaRecorderType;
  withBlob: (blobUrl: string, blob: Blob) => void;
}) => {
  const { type, withBlob } = props;

  return (
    <div>
      <ReactMediaRecorder
        screen={type === "screen"}
        audio
        blobPropertyBag={{ type: type === "audio" ? "audio/wav" : "video/mp4" }}
        // audio={type === "audio"}
        video={type === "video"}
        stopStreamsOnStop
        onStop={(blobUrl, blob) => {
          withBlob(blobUrl, blob);
        }}
        render={(props) => <MediaRecorderComponent {...props} type={type} />}
      />
    </div>
  );
}
