export const getBundleAugmentedWords = async (): Promise<AugmentedWord[]> => {
  // NB: we need to cast the string because in non-sensible-projects this might otherwise fail
  const bundles: AugmentedAnyModelType[] = await db.get(
    "BundleConfig" as keyof DbModels
  );

  const bundleAugmentedWords: AugmentedWord[] = bundles.map((bundle) => {
    const augmentedWord: AugmentedWord = {
      queryPath: bundle.docsRelativeFolderPath,
      spoiler: bundle.description,
      type: "bundle",
      word: bundle.slug,
      projectRelativeMarkdownSourcePath: bundle.projectRelativePath,
    };

    return augmentedWord;
  });

  return bundleAugmentedWords;
};
