async (
  manualProjectRoot?: string
): Promise<MappedObject<AugmentedWord> | undefined> => {
  const projectRoot = manualProjectRoot || getProjectRoot();

  if (!projectRoot) return;

  if (cache) return cache;

  const augmentedWordsSerializable = (await getAugmentedWords(projectRoot)).map(
    (item) => omitUndefinedValues(item)
  );

  const mappedObject = createMappedObject(augmentedWordsSerializable, "word");

  cache = mappedObject;
  return mappedObject;
}
