export const PictureWithInfoDropdown = <T extends unknown>(props: {
  current: PictureWithInfo<T>;
  dropdown: PictureWithInfo<T>[];
  extraItems?: { onClick: () => void; text: string }[];
  isLoading: boolean;
  /**
   * if not given, dropdown is opened
   */
  onClickCurrent?: () => void;
  onSelectDropdownItem: (selected: PictureWithInfo<T>["data"]) => void;
}) => {
  const {
    current,
    dropdown,
    isLoading,
    onClickCurrent,
    onSelectDropdownItem,
    extraItems,
  } = props;

  const router = useRouter();

  const [showDropdown, setShowDropdown] = useState(false);

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const pictureUrl =
    current.pictureUrl ||
    getAssetDirectlyApiUrl("public/frontend/default-profile-picture.png");

  return (
    <Div className="relative">
      <Div
        className="cursor-pointer flex flex-row justify-end pr-2 items-center gap gap-2"
        onClick={onClickCurrent || toggleDropdown}
      >
        {isLoading ? (
          <FancyLoader />
        ) : (
          <Image
            src={pictureUrl}
            className="rounded-full w-8 h-8 border border-black p-0.5 bg-gray-400"
          />
        )}
        <P>{current.info}</P>
      </Div>
      <Div className="relative">
        {showDropdown ? (
          <Div className="absolute top-0 right-0 bg-slate-400 min-w-[200px] rounded-md">
            <b>Your accounts</b>
            {dropdown.map((item) => {
              return (
                <Div
                  className={`${
                    item.isCurrent
                      ? "bg-slate-600"
                      : "hover:rounded-md hover:bg-slate-600"
                  } px-2 cursor-pointer py-1`}
                  onClick={() => {
                    if (item.isCurrent) return;
                    if (item.url) {
                      router.push(item.url);
                      return;
                    }
                    toggleDropdown();
                    onSelectDropdownItem(item.data);
                  }}
                >
                  {item.info}
                </Div>
              );
            })}

            <Div className="h-4" />
            <b>More</b>

            {extraItems?.map((item) => {
              return (
                <Div
                  className="hover:bg-slate-600 hover:rounded-md text-xs cursor-pointer p-2"
                  onClick={() => {
                    toggleDropdown();
                    item.onClick();
                  }}
                >
                  {item.text}
                </Div>
              );
            })}
          </Div>
        ) : null}
      </Div>
    </Div>
  );
};
