() => {
  const modelName = useModelFromUrl();

  const [datasetConfig] = useStore("db-crud.datasetConfig");
  const [search] = useStore("db-crud.search");

  const dependencies = ["getDbModel", modelName, datasetConfig, search];

  const hook = useInfiniteQuery(
    dependencies,
    (context) => {
      const params = context.pageParam as PageParam | undefined;

      const configuration = {
        ...datasetConfig,
        startFromIndex:
          (datasetConfig?.startFromIndex || 0) + (params?.start || 0),
        maxRows: datasetConfig?.maxRows || params?.limit || defaultLimit,
      };

      // @ts-ignore
      const result = getDbModel(modelName!, configuration, search);
      return result;
    },
    {
      getNextPageParam: (lastPage, allPages) => {
        const realLimit =
          lastPage.result?.datasetConfig?.maxRows || defaultLimit;
        const oldStart = lastPage.result?.datasetConfig?.startFromIndex || 0;
        const pageParam: PageParam = {
          start: oldStart + realLimit,
          limit: realLimit,
        };

        return pageParam;
      },
    }
  );

  return hook;
}
