type ValidationResult = {
  isValid: boolean;
  errors?: { fieldStack: string[]; error: string }[];
};

export const validateResult = <TFunctionName extends keyof typeof sdk>(
  functionName: TFunctionName,
  result: any,
  tsFunction: TsFunction
): ValidationResult => {
  const fn = sdk[functionName];

  type ResultType = ReturnType<typeof fn>;
  /**
   * NB: https://www.npmjs.com/package/typescript-json
   *
   * this seems a great way to validate the return type, not sure what it would do to my compile times and runtime performance and if it works at all, but if it works, it'd be great because we basically create 100% typesafety, not only for the input, but also for the output!
   */
  // const isValid = TSON.equal<ResultType>(result)

  const schema = tsFunction.returnType.typeDefinition;

  if (!schema) {
    log(`Not able to validate ${functionName} result`, { type: "debug" });
    return { isValid: true };
  }

  //const validate = ajv.compile(schema);

  const isValid = true; //validate(result);

  if (!isValid) {
    log(
      `According to the schema, you have given invalid arguments to the function`,
      { type: "warning" }
    );
  }

  return { isValid };
};
