export const getInstanceNames = (
  array: any[],
  // NB: I tried with  <T extends TsIndexModelType> but didn't work with a map over an object... strange...
  relativePathFromProjectRoot: string
): string[] => {
  const instances = array.filter(
    hasSameProjectPath(relativePathFromProjectRoot)
  );

  const instancePaths = instances.map((x) => x.name);
  return instancePaths;
};

export const hasSameProjectPath =
  (projectRelativePath: string) =>
  <T extends TsIndexModelType>(x: T) => {
    const isSame = x.projectRelativePath === projectRelativePath;
    return isSame;
  };
