 async (
  config?: TodoPagesConfig
): Promise<{ nested: NestedWebPage[]; flat: (WriterWebPage | WebPage)[] }> => {
  // console.log({ config });
  const projectRoot = getProjectRoot();
  if (!projectRoot) return { nested: [], flat: [] };

  //needed for matching persons but also for just showing that it has offers
  const todoOffers = await db.get("TodoOffer");
  const todoPaths = await getTodoPaths(config);

  const todoPages: WriterWebPage[] =
    todoPaths?.map((projectRelativeFilePath) => {
      const [before, after] = projectRelativeFilePath.split("/todo/");
      const lastFolderBeforeTodo = getLastFolder(before);
      const todoRelativeFileId = withoutExtension(after);
      const fileName = getLastFolder(todoRelativeFileId);
      /**
       * Idea: parsing the file for amount of items with and without checkmark, showing the % done and (x/y)
       */
      const menuTitle = humanCase(fileName);

      const hasTodoOffers = !!todoOffers.find(
        (x) => x.todoFileId === projectRelativeFilePath
      );
      /**
       * showing an emoji the status of the todo-offers
       */
      const menuTitleAugmentation = hasTodoOffers ? "ðŸ”¥" : undefined;

      /**
       * Idea: showing a summary, more about the progress and owner(s), and anything you just quickly wanna see
       */
      const menuTitleTooltip =
        "Tooltip example\n\nShould render markdown\n\n**Say,this is great, no?**";

      const page: WriterWebPage = {
        pageData: {
          projectRelativeFilePath,
        },
        queryPath: `${lastFolderBeforeTodo}/${todoRelativeFileId}`,
        menuTitle,
        menuTitleAugmentation,
        menuTitleTooltip,
        isMenuHidden: false,
      };

      return page;
    }) || [];

  const otherPages: WebPage<unknown>[] = [
    {
      queryPath: "stats",
      menuTitle: "Statistics",
      menuTitleTooltip: "Some stats about cool stuff",
      pageData: undefined,
    },

    {
      queryPath: "SelfSprintReview",
      menuTitle: "Sprint review",
      menuTitleTooltip: "Let's do this every friday, 3PM CET",
      pageData: undefined,
    },
    {
      queryPath: "TodoOffer",
      isMenuHidden: true,
      pageData: undefined,
    },
    {
      queryPath: "upsert/TodoOffer",
      isMenuHidden: true,
      pageData: undefined,
    },
    {
      queryPath: "upsert/SelfSprintReview",
      isMenuHidden: true,
      pageData: undefined,
    },
  ];

  const flat = otherPages.concat(todoPages) as (WriterWebPage | WebPage)[];

  // nestifyQueryPathObjectRecursive(flatWithoutPageData);

  return getMenuPagesObject(flat);
}
