async (operationBasePath: string) => {
  const srcPath = path.join(operationBasePath, "src");
  const docsPath = path.join(operationBasePath, "docs");

  const rootExploreResults = await explore({
    basePath: operationBasePath,
    ignore: ".DS_Store",
    doNotExploreChildFolders: true,
    extension: ["md", "mdx"],
  });

  const docsExploreResults = await explore({
    basePath: docsPath,
    ignore: ".DS_Store",
    extension: ["md", "mdx"],
    includeFoldersWithResults: true,
  });

  const docsExploreResultsWithBasePath =
    docsExploreResults.length > 0
      ? docsExploreResults.concat([{ path: docsPath, isFolder: true }])
      : [];

  const srcExploreResults = (
    await explore({
      basePath: srcPath,
      ignore: ".DS_Store",
      extension: ["ts", "tsx"],
      includeFoldersWithResults: true,
    })
  )
    // NB: filter out index files, they're not interesting for this usecase
    .filter((x) => !x.path.includes("/src/index.ts"));

  const srcExploreResultsWithBasePath =
    srcExploreResults.length > 0
      ? srcExploreResults.concat([{ path: srcPath, isFolder: true }])
      : [];

  return rootExploreResults
    .concat(docsExploreResultsWithBasePath)
    .concat(srcExploreResultsWithBasePath);
}
