async (
  operationName: string,
  already?: string[]
): Promise<string[]> => {
  const dependantOperationNames = await findDependants({
    returnOperationName: true,
    operationName,
  });

  const dependantsUntilNow = [
    operationName,
    ...dependantOperationNames,
    ...(already || []),
  ];

  const recursedDependants: string[] =
    dependantOperationNames.length > 0
      ? (
          await Promise.all(
            dependantOperationNames.map((name) =>
              findDependantsRecursively(name, dependantsUntilNow)
            )
          )
        ).flat()
      : [];

  return [...dependantsUntilNow, ...recursedDependants].filter(onlyUnique2());
}
