value:
async (
  /**
   * all imports
   */
  imports: TsImport[],
  packageJsons: PackageJson[],
  operationName: string,
  /**
   * skip recursing on these ones because they are already found
   */
  already: string[],
  ignore?: string[],
  ignoreFilter?: (operationName: string) => boolean
): Promise<string[]> => {
  if (ignore?.includes(operationName)) return [];

  const packageJson = packageJsons.find(
    (x) => x.operationName === operationName
  );

  if (!packageJson) {
    console.log(
      `WARN: couldn't find packagejson ${operationName}. weird...`,
      packageJsons.slice(0, 5)
    );
    return [];
  }

  const indirectDependencies =
    packageJson.operation?.indirectDependencies || [];

  /**
   * Filter out ignored ones, either by ignore prop or ignore filter
   */
  const onlyIncluded = (operationName: string) =>
    !ignore?.includes(operationName) &&
    (!ignoreFilter || ignoreFilter(operationName));

  const thisImports = imports.filter((x) => x.operationName === operationName);

  const monorepoModules = thisImports
    .map((x) =>
      x.isModuleFromMonorepo && x.isModuleResolved ? x.module : null
    )
    .filter(notEmpty)
    .filter(onlyIncluded)
    // add the indirect ones
    .concat(indirectDependencies);

  const newAlready = already.concat(monorepoModules).filter(onlyUnique);

  const dependencyPromises = monorepoModules
    .filter((x) => !already?.includes(x))
    .map((operationName) =>
      findDependenciesRecursively(
        imports,
        packageJsons,
        operationName,
        newAlready,
        ignore
      )
    );
  const dependencies = (await Promise.all(dependencyPromises)).flat();
  const all: string[] = monorepoModules.concat(dependencies);
  return all;
}
