async ({
  folderPath,
}: {
  folderPath: string;
}): Promise<number> => {
  return (
    await explore({
      basePath: folderPath,
      includeStats: true,
      extension: ["ts", "tsx"],
      ignore: "index.ts",
    })
  ).reduce((updatedAt, file) => {
    const fileUpdatedAt = file.stats?.updatedAt;
    if (!fileUpdatedAt) return updatedAt;
    if (fileUpdatedAt > updatedAt) return fileUpdatedAt;
    return updatedAt;
  }, 0);
}
