value:
(
  bareItem: Creation<AugmentedAnyModelType>,
  isKvmdStorage?: boolean
): AugmentedAnyModelType => {
  const now = Date.now();

  const defaultValues = {
    createdAt: now,
    updatedAt: now,
    deletedAt: 0,
    createdFirstAt: now,
  };

  const shouldCreateSlugFromName =
    !!bareItem.name && bareItem.slug === undefined;

  const slugifiedSlug = bareItem.slug ? slugify(bareItem.slug) : undefined;

  const isValidSlug =
    bareItem.slug === undefined || slugifiedSlug === bareItem.slug;

  if (!isValidSlug) {
    log(
      `${bareItem.slug} is not a slugified slug, it has been altered to a valid slug`,
      { type: "warning" }
    );
  }

  const slug: string | undefined = shouldCreateSlugFromName
    ? slugify(bareItem.name)
    : slugifiedSlug;

  const id =
    bareItem.id === undefined
      ? isKvmdStorage
        ? kebabCase(bareItem.name)
        : generateId()
      : bareItem.id;

  const item = {
    ...defaultValues,
    ...bareItem,
    slug,
    id,
  } as AugmentedAnyModelType;

  return item;
}
