async (options: {
  modelName: string;
  parsedPath: ParsedPath;
  operationName: string | null;
  projectRoot: string;
  rootFolder: RootDbFolder;
}): Promise<DbFileLocation[]> => {
  const { modelName, operationName, parsedPath, projectRoot, rootFolder } =
    options;
  if (fs.existsSync(parsedPath.dir)) {
    // if parsedPath ends with *, read everything in the folder
    const fileNames: DbFileLocation[] = (await fs.readdir(parsedPath.dir))
      .filter((fileName) => fileName.endsWith(parsedPath.ext))
      .map((fileName) => {
        const absolutePath = path.join(parsedPath.dir, fileName);
        const projectRelativePath = absolutePath.substring(projectRoot.length);
        const operationRelativePath = absolutePath.substring(
          rootFolder.basePath.length
        );
        const dbFileLocation: DbFileLocation = {
          modelName,
          absolutePath,
          operationName: rootFolder.operationName,
          projectRelativePath,
          operationRelativePath,
        };

        return dbFileLocation;
      });

    return fileNames;
  }

  return [];
}
