export const getPossibleModelFiles = (modelName: string): string[] => {
  const modelNameKebabCase = kebabCase(modelName);
  return [
    `${modelNameKebabCase}.json`,
    `${modelNameKebabCase}s.json`,
    `${modelNameKebabCase}.csv`,
    `${modelNameKebabCase}s.csv`,
    `${modelNameKebabCase}.md`,
    `${modelNameKebabCase}s.md`,
  ];
};
