export const getSubExtension = (filename: string): string | undefined => {
  const parts = filename.split(".");
  //removes extension
  parts.pop();
  //returns extension that's left (the sub-extension)
  const subExtension = parts.pop();
  return subExtension;
};

export const withoutExtension = (fileName: string) => {
  const pieces = fileName.split(".");
  pieces.pop();
  return pieces.join(".");
};

export const getExtension = (fileNameOrPath: string) => {
  const pieces = fileNameOrPath.split(".");
  return pieces.pop()!;
};
