export const FunctionForm = <T extends (...params: any[]) => any>(props: {
  /**
   * Must be given if you want to store assets with this
   */
  projectRelativeStorageFilePath?: string;
  /**
   * Must be given if you want to store assets with this
   */
  modelName?: string;
  /**
   * Add the tsfunction here imported from `your-operation/db/ts-functions/[function-name].json` directly
   *
   * Typed as `any` because you can import it from JSON, but this should be a `TsFunction`.
   *
   * @see TsFunction
   */
  tsFunction: any;
  /**
   * For js functions, provide the function to be executed here. If not given, it will try to use the api.
   *
   * NB: this should be the same function as the function you provide the index of at `tsFunction`
   */
  submitFunction?: T;
  /**
   * If you provide a `submitFunction`, you can also provide a callback that does something with the result of the function
   */
  withResult?: (result: WithoutPromise<ReturnType<T>>) => void;
  /**
   * callback to call after the api has resulted with a result
   *
   * Please type this yourself as `RealApiReturnType<"functionName">`
   *
   * If you don't provide this but an api call is done, we'll call `showStandardResponse`
   */
  withApiResult?: (result: any) => void;
  initialValues?: any[];
  showResult?: boolean;
  /**
   * Provide referencable model data if you want to
   */
  referencableModelData?: ReferencableModelData;
}) => {
  const {
    initialValues,
    modelName,
    projectRelativeStorageFilePath,
    tsFunction,
    submitFunction,
    withResult,
    withApiResult,
    showResult,
    referencableModelData,
  } = props;
  const { parameters, name } = tsFunction;
  const isFirstParameterFunctionContext =
    parameters?.[0]?.name === "functionContext";

  const slicedParameters = isFirstParameterFunctionContext
    ? parameters?.slice(1)
    : parameters;

  const [isLoading, setIsLoading] = useState(false);

  const [values, setValues] = useState<any[]>(
    slicedParameters?.map((_: any, index: number) => initialValues?.[index])
  );

  const [result, setResult] = useState<RealApiReturnType<any> | undefined>(
    undefined
  );

  if (!slicedParameters) {
    console.log({
      tsFunction,
      parameters,
      slicedParameters,
      isFirstParameterFunctionContext,
    });
    return (
      <Div>No parameters found, please find the tsFunction in console</Div>
    );
  }

  const renderResult = () => {
    return (
      <Div>
        {result?.isUnauthorized ? (
          <P className="text-red-500 font-bold">Unauthorized!</P>
        ) : null}
        {result?.errors?.map((error) => (
          <P className="text-red-500">{error.error}</P>
        ))}
        {result?.message ? (
          <P
            className={result.isSuccessful ? "text-green-500" : "text-red-500"}
          >
            {result.message}
          </P>
        ) : null}

        {result?.result ? (
          <textarea
            className="w-full h-[300px]"
            value={JSON.stringify(result.result, undefined, 2)}
          />
        ) : null}
      </Div>
    );
  };
  return (
    <Div>
      <FormContainer
        isLoading={isLoading}
        onSubmit={async () => {
          setIsLoading(true);

          if (submitFunction) {
            //@ts-ignore
            const result = await submitFunction(...values);
            withResult?.(result);

            setIsLoading(false);

            return;
          }

          // @ts-ignore
          const apiFunction = api[name];

          if (!apiFunction || typeof apiFunction !== "function") {
            showStandardResponse({
              isSuccessful: false,
              message: "Could not find this function in the SDK",
            });
            setIsLoading(false);
            return;
          }

          // Apply it if it's there
          const apiResult = await apiFunction(...values);
          setResult(apiResult);

          if (withApiResult) {
            withApiResult(apiResult);
          } else {
            showStandardResponse(apiResult);
          }

          setIsLoading(false);
        }}
      >
        {showResult ? renderResult() : null}

        <SimplifiedSchemaForm
          projectRelativeStorageFilePath={projectRelativeStorageFilePath}
          modelName={modelName}
          parameters={slicedParameters}
          id={name}
          onChange={(values) => {
            setValues(values);
          }}
          values={values}
          referencableModelData={referencableModelData}
        />
      </FormContainer>
    </Div>
  );
};
