(
  operationRelativeSourcePath: string
) => {
  // 1) remove src/
  const withouSrc = operationRelativeSourcePath.startsWith("src/")
    ? operationRelativeSourcePath.slice("src/".length)
    : undefined;

  const folder = getFolderJs(withouSrc);

  if (folder === "") return undefined;

  return folder;
}
