value:
(
  absolutePath: string
): OperationPathParse | undefined => {
  if (!absolutePath) return;
  const pathParse = getPathParse(absolutePath);
  if (!pathParse) return;
  const projectRoot = getProjectRoot(absolutePath);
  if (!projectRoot) return;
  const operationBasePath = findOperationBasePath(absolutePath);
  if (!operationBasePath) return;
  const operationSrcPath = path.join(operationBasePath, "src");

  const operationFolderName = getLastFolder(operationBasePath);
  const operationName = readJsonFileSync<PackageJson>(
    path.join(operationBasePath, "package.json")
  )?.name;

  const operationRelativeTypescriptFilePath = getOperationRelativePath(
    absolutePath,
    operationBasePath
  );

  const parsedPath = path.parse(absolutePath);
  const srcFileId = makeRelative(
    path.join(parsedPath.dir, parsedPath.name),
    operationSrcPath
  );

  const relativeOperationBasePathFromProjectRoot = operationBasePath.slice(
    projectRoot.length
  );

  return {
    relativePathFromProjectRoot: pathParse.relativePathFromProjectRoot,
    srcFileId,
    operationFolderName,
    operationRelativeTypescriptFilePath,
    relativeOperationBasePathFromProjectRoot,
    operationName,
  };
}
