value:
(config?: {
  /**
   * if given, this will be taken as the project root instead of the one that can be found automatically (useful for bundling or working with multiple projects that alter each other)
   */
  manualProjectRoot?: string;
}): string[] => {
  const rootPath = config?.manualProjectRoot || getProjectRoot();

  if (!rootPath) {
    log(`no rootpath found!`, { type: "error" });
    process.exit(1);
  }

  if (isBundle(rootPath)) {
    const pathsWithOperations = ["apps", "packages", "modules"]
      .map((folderName) => path.join(rootPath, folderName))
      .filter((fullPath) => fs.existsSync(fullPath));

    if (pathsWithOperations.length === 0) {
      log(`Couldn't find any operations in ${rootPath}`);
      process.exit(1);
    }

    return pathsWithOperations;
  }
  const toolsPath = path.join(rootPath, "operations/tools");
  const bundlesPath = path.join(rootPath, "operations/niches");
  if (!fs.existsSync(toolsPath) || !fs.existsSync(bundlesPath)) {
    log(`Couldn't find tools or bundles folder in ${rootPath}`);
    process.exit(1);
  }

  return [toolsPath, bundlesPath];
}
