value:
(
  folderPath: string
): undefined | { isSensibleProject: boolean; isWorkspaceRoot: boolean } => {
  const packageJsonPath = path.join(folderPath, "package.json");
  const existsPackageJson = fs.existsSync(packageJsonPath);
  if (!existsPackageJson) return;

  const packageJson = tryParseJson<PackageJson>(
    fs.readFileSync(packageJsonPath, "utf8")
  );

  const sensibleConfig: SensibleConfig | undefined = packageJson?.sensible;
  const isSensibleProject = sensibleConfig?.isSensibleProject || false;

  if (!packageJson || !packageJson.workspaces) {
    return;
  }

  return { isSensibleProject, isWorkspaceRoot: true };
}
