export const getIndexFilePath = (
  srcFilePath: string,
  indexInstance: keyof TypescriptIndex
) => {
  const projectRoot = getProjectRoot(srcFilePath);
  const parse = getOperationPathParse(srcFilePath);

  if (!projectRoot || !parse) return;

  const indexFilePath = path.join(
    projectRoot,
    parse.relativeOperationBasePathFromProjectRoot,
    "index",
    kebabCase(indexInstance),
    `${parse.srcFileId}.json`
  );
  return indexFilePath;
};
