export const isProjectRoot = (folderPath: string) => {
  const packageJsonPath = path.join(folderPath, "package.json");
  const existsPackageJson = fs.existsSync(packageJsonPath);
  if (!existsPackageJson) return false;

  const packageJson = tryParseJson<PackageJSON>(
    fs.readFileSync(packageJsonPath, "utf8")
  );

  if (!packageJson || !packageJson.workspaces) {
    return false;
  }

  return true;
};
