rawText:
 (folderPath?: string): boolean => {
  const realFolderPath = folderPath || getProjectRoot();
  if (!realFolderPath) return false;

  // get package-json and check sensible config
  const packageJson = readJsonFileSync<PackageJSON>(
    path.join(realFolderPath, "package.json")
  );

  const sensibleConfig: SensibleConfig | undefined = packageJson?.sensible;
  const isSensibleProject = sensibleConfig?.isSensibleProject || false;

  return isSensibleProject;
}
