(string: string): SizeSummary => {
  const characters = string.length;
  const lines = string.split("\n").length;
  const bytes = byteCount(string);
  return {
    characters,
    lines,
    bytes,
    bytesPerCharacter: bytes / characters,
    charactersPerLine: Math.round(characters / lines),
    linesPerFile: lines,
    numberOfFiles: 1,
  };
}
