async (
  eventName,
  filePath
) => {
  // NB: need to provide get folder because the original file is gone so it can't be detected
  const operationPath = findOperationBasePath(getFolder(filePath));
  if (!operationPath) {
    return;
  }

  const operationName = getLastFolder(operationPath);
  // console.log(`Found operationName: ${operationName}...`);

  const result = await cleanupTsDatabase(operationName);
  console.log(
    `Cleaned up ${result?.amountRemoved || 0} indexes (removed ${
      path.parse(filePath).base
    })`
  );
}
