(a: string, b: string) => {
  const lettersLeft = a
    .split("")
    .reduce((lettersLeft, lowercaseValueLetter) => {
      if (lettersLeft[0] === lowercaseValueLetter) {
        lettersLeft.shift();
      }

      return lettersLeft;
    }, b.split(""));

  return lettersLeft.length === 0;
}
