value:
async <T, U>(
  mapFn: MapFn<T, U>,
  currentValue: T,
  index: number,
  array: T[]
): Promise<{
  status: "fulfilled" | "rejected";
  value?: U;
  reason?: unknown;
}> => {
  try {
    return {
      status: "fulfilled",
      value: await mapFn(currentValue, index, array),
    };
  } catch (reason) {
    return {
      status: "rejected",
      reason,
    };
  }
}
