value:
(
  preset: "packages" | "markdown" | "todo" | "docs" | "src" | "git",
  config?: BaseConfig
) => {
  const basePath = config?.basePath;

  const searchConfigs: SearchConfig[] =
    preset === "packages"
      ? [
          {
            basePath,
            search: "package.json",
            exact: true,
            extension: "json",
            ignore: generatedFolders,
          },
        ]
      : preset === "markdown"
      ? [
          {
            basePath,
            extension: ["md", "mdx"],
            ignore: generatedFolders,
          },
        ]
      : preset === "todo"
      ? // finds all `*.todo.md`,` todo/**/*.md` and returns the path + content

        [
          {
            basePath,
            extension: ["md", "mdx"],
            search: "/todo/",
            searchLevel: "filePath",
            ignore: generatedFolders,
          },
          {
            basePath,
            extension: ["md", "mdx"],
            search: "todo.md",
            exact: true,
            searchLevel: "fileName",
            ignore: generatedFolders,
          },
          {
            basePath,
            extension: ["md", "mdx"],
            subExtension: "todo",
            exact: false,
            searchLevel: "fileName",
            ignore: generatedFolders,
          },
        ]
      : preset === "docs"
      ? [
          {
            basePath,
            extension: ["md", "mdx"],
            search: "/docs/",
            searchLevel: "filePath",
            ignore: generatedFolders,
          },
          {
            basePath,
            extension: ["md", "mdx"],
            search: "readme.md",
            exact: true,
            searchLevel: "fileName",
            ignore: generatedFolders,
          },
          {
            basePath,
            extension: ["md", "mdx"],
            subExtension: ["readme"],
            exact: false,
            searchLevel: "fileName",
            ignore: generatedFolders,
          },
        ]
      : preset === "src"
      ? [
          {
            basePath,
            searchLevel: "folder",
            exact: true,
            search: "src",
            doNotExploreMatch: true,
            ignore: generatedFolders,
          },
        ]
      : [];

  const textJsons = exploreMultiple(searchConfigs);
  return textJsons;
}
