export const parseKvmdLine = (string: string): KvmdLine | undefined => {
  const htmlCommentRegex = /<!--[\s\S]*?-->/g;
  const lineWithoutComment = string.replace(htmlCommentRegex, "");

  const commentMatch = string.match(htmlCommentRegex)?.[0];
  const comment = commentMatch
    ? commentMatch.replace("<!--", "").replace("-->", "").trim()
    : null;

  const [keyString, valueString] = lineWithoutComment
    .split(":")
    .map((x) => x.trim());

  const value = valueString?.length ? valueString : undefined;
  const slug = kebabCase(keyString);

  if (keyString.length === 0) return;

  return {
    id: slug,
    name: keyString,
    value,
    comment,
    slug,
  };
};
