rawText:
 (
  kvmdData: KeyValueMarkdownModelType,
  previous: KeyValueMarkdownModelType | undefined
) => {
  // headers only need a newline before it if it's not the first item (so there is a previous)
  const newlineIfHeader = kvmdData.isHeaderCalculated && !!previous ? "\n" : "";

  const level = kvmdData.isHeaderCalculated
    ? kvmdData.categoryStackCalculated.length + 1
    : 0;

  const headerPrefix = kvmdData.isHeaderCalculated
    ? "#".repeat(level).concat(" ")
    : "";

  const isFirstItem =
    !kvmdData.isHeaderCalculated && previous?.isHeaderCalculated;
  const itemPrefix = isFirstItem ? "\n" : "";

  const line = `${newlineIfHeader}${headerPrefix}${itemPrefix}${
    kvmdData.name ? `${kvmdData.name}: ` : ""
  }${kvmdData.value || ""}${
    kvmdData.comment ? ` <!-- ${kvmdData.comment} -->` : ""
  }\n`;

  return line;
}
