value:
(
  chunk: MarkdownChunk,
  categoryStackCalculatedUntilNow?: CategoryStack
): Storing<KeyValueMarkdownModelType>[] => {
  // NB: copy
  let categoryStackCalculated = categoryStackCalculatedUntilNow
    ? [...categoryStackCalculatedUntilNow]
    : [];

  let allKvmdItems: Storing<KeyValueMarkdownModelType>[] = [];

  // If this chunk has a title (this is a header)
  if (chunk.title) {
    const headerParsedKvmdLine = parseKvmdLine(chunk.title);

    // NB: titles with an empty key are ignored
    if (headerParsedKvmdLine) {
      // NB: also add the title as a model item
      const headerKvmdItem: Storing<KeyValueMarkdownModelType> = {
        ...headerParsedKvmdLine,
        isHeaderCalculated: true,
        // NB: copy!
        categoryStackCalculated: [...categoryStackCalculated],
      };
      allKvmdItems.push(headerKvmdItem);

      // NB: the slug of the title is added to the categoryStackCalculated, but only AFTER adding the kvmd item of the title
      categoryStackCalculated.push(headerParsedKvmdLine.slug);
    }
  }

  const childKvmdItems =
    chunk.children
      ?.map((childChunk) =>
        getKvmdItemsRecursively(childChunk, categoryStackCalculated)
      )
      .flat() || [];

  allKvmdItems = allKvmdItems.concat(childKvmdItems);

  const lineKvmdItems = (chunk.content || "")
    // NB: we are splitting on newlines here because content can still contain newlines.
    .split("\n")
    .map((line) => {
      const parsedKvmdLine = parseKvmdLine(line);
      if (!parsedKvmdLine) return;

      const contentKvmdItem: Storing<KeyValueMarkdownModelType> = {
        ...parsedKvmdLine,
        isHeaderCalculated: false,
        categoryStackCalculated,
      };

      return contentKvmdItem;
    })
    .filter(notEmpty);

  allKvmdItems = allKvmdItems.concat(lineKvmdItems);

  return allKvmdItems;
}
