(
  kvMdString: string,
  dbFileLocation: DbFileLocation
): KeyValueMarkdownParse => {
  const { parameters, content } = mdToJsonParse(kvMdString);
  const { absolutePath, modelName, ...modelLocation } = dbFileLocation;

  const parentKey = `parent_${camelCase(modelName)}Slug`;

  if (!content) {
    console.log("NO CONTENT FOUND");
    return { parameters: {}, data: [] };
  }

  const kvmdItems = content
    .map((chunk) => getKvmdItemsRecursively(chunk))
    .flat();

  const data: KeyValueMarkdownModelType[] = kvmdItems.map((x) => ({
    ...x,
    ...modelLocation,
    // NB: here we set the reference to its parent (which is equal to the last item in the categoryStackCalculated)
    [parentKey]:
      x.categoryStackCalculated[x.categoryStackCalculated.length - 1],
  }));

  return { parameters, data };
}
