export const getParagraphsRecursively = (
  chunk: MarkdownChunk,
  categoryStackUntilNow?: CategoryStack
): MarkdownParagraph[] => {
  const categoryStack = chunk.title
    ? (categoryStackUntilNow || []).concat([chunk.title])
    : categoryStackUntilNow || [];
  const paragraphs =
    chunk.children
      ?.map((childChunk) => getParagraphsRecursively(childChunk, categoryStack))
      .flat() || [];
  return [{ paragraph: chunk.content || "", categoryStack }, ...paragraphs];
};
