export const LayoutGrid = (props: {
  children: React.ReactNode;
  header: React.ReactNode;
  /**
   * Menu is only rendered if you pass it here
   */
  menu?: MenuProps;
}) => {
  const { children, header, menu } = props;

  const [isMobileMenuEnabled] = useStore("menu.isMobileMenuEnabled");

  const renderMenu = () => {
    return menu ? <Menu {...menu} message={undefined} /> : null;
  };

  return (
    <main className="dark:bg-zinc-900 dark:text-white relative">
      {/* mobile layout */}
      <Div className="lg:invisible absolute overflow-y-scroll top-0 left-0 w-screen h-screen">
        {header}
        {isMobileMenuEnabled ? <Div>{renderMenu()}</Div> : children}
      </Div>

      {/* Big screen layout */}
      <Div
        className={`w-screen h-screen flex flex-1 invisible lg:visible flex-col`}
      >
        {header}

        <Div
          className={`h-full overflow-hidden flex-1 flex ${
            menu?.menuPosition === "left" ? "flex-row-reverse" : "flex-row"
          }`}
        >
          <Div className="min-h-0 overflow-y-scroll w-full h-full inline-block">
            {children}
          </Div>
          {menu ? (
            <Div className="inline-block min-h-0 overflow-y-scroll h-full w-80">
              {renderMenu()}
            </Div>
          ) : null}
        </Div>
      </Div>
    </main>
  );
};
