() => {
  const [isOffline, setIsOffline] = useState(false);
  useEffect(() => {
    const intervalId = setInterval(() => {
      getItem(API_NO_RESPONSE_TIME_KEY).then((value) => {
        if (!value) {
          if (isOffline) {
            setIsOffline(false);
            return;
          }
        }

        const lastNoResponse = Number(value);
        const timeAgo = Date.now() - lastNoResponse;
        if (timeAgo < timeBeforeNotOffline && isOffline) {
          setIsOffline(false);
        } else {
          setIsOffline(true);
        }
      });
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

